import io from 'socket.io-client';
import { Link, useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import {variables} from "../Variables";
import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'

function AppConfirm(){

  const [input, setInput] = useState('');
  const [message, setMessage] = useState('');
  const socket = io('http://localhost:5001');
  const socket0 = io('http://localhost:5001');

  useEffect(()=>{
    socket.emit('join', this.state.params.get("id"));

    if(this.state.params.get("start") === '1'){
      console.log(this.state.params.get("start"));
      socket0.emit('join', this.state.params.get("id"));
      socket0.emit('start', this.state.params.get("id"));
    }

    // Listen for event from the server
    socket.on('time', (message) => {
      this.setState({ messages: message });
    });

    socket.on('cancel', (message) => {
      this.setState({ messages: message });
    });

    socket.on('confirm', (message) => {
      this.setState({ messages: message });
    });

    socket.on('msg', (message) => {
      this.setState({ messages: message });
    });

    socket0.on('cancel', (message) => {
      console.log(0)
      socket0.emit('cancel', this.state.params.get("id"));
    });

    socket0.on('confirm', (message) => {
      console.log(0)
      socket0.emit('confirm', this.state.params.get("id"));
    });
  })

  async function sendMessage() {
    const { input } = this.state;
    // Emit event to the server
    socket.emit('join', input);
    this.setState({ id: input });
  }

  async function sendConfirm() {
    // Emit event to the server
    if(this.state.params.get("start") === '1'){
      socket0.emit('confirm', this.state.params.get("id"));
    } else
    socket.emit('confirm_', this.state.params.get("id"));
    // window.location.replace(window.location.origin)
  }

  async function sendCancel() {
    // Emit event to the server
    if(this.state.params.get("start") === '1'){
      socket0.emit('cancel', this.state.params.get("id"));
    } else
    socket.emit('cancel_', this.state.params.get("id"));
    // window.location.replace(window.location.origin)
  }

  handleChange = (e) => {
    this.setState({ input: e.target.value });
  }

    return (
        <div>
        <input value={input} onChange={this.handleChange} />
        <button onClick={this.sendMessage}>Send</button>
        <button onClick={this.sendConfirm}>confirm</button>
        <button onClick={this.sendCancel}>cancel</button>
        <ul>
            {messages}
        </ul>
        </div>
    );
}

export default AppConfirm;
