import io from 'socket.io-client';
import { Link, useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import {variables} from "../Variables";
import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'

const socket = io('http://localhost:5001');
const socket0 = io('http://localhost:5001');
const MySwal = withReactContent(Swal);

function AppConfirm(){
    const {id, start} = useParams();
    const [input, setInput] = useState('');
    const [messages, setMessages] = useState('');
    const [order, setOrder] = useState(null);

    useEffect(()=>{
        console.log(id)
        console.log(start)
        socket.emit('join', id);

        if(start === '1'){
            socket0.emit('join', id);
            socket0.emit('start', id);
        }

        // Listen for event from the server
        socket.on('time', (message) => {
        setMessages(message)
        });

        socket.on('cancel', (message) => {
        setMessages(message)
        });

        socket.on('confirm', (message) => {
        setMessages(message)
        });

        socket.on('msg', (message) => {
        setMessages(message)
        });

        socket0.on('cancel', (message) => {
        console.log(0)
        socket0.emit('cancel', id);
        MySwal.fire({
            html: 
                '<p style="text-align: left; margin: 0 auto;max-width: fit-content;">'+
                message+
                '</p>',
            icon: "error",
            confirmButtonColor: "#F07221"
        })
        });

        socket0.on('confirm', (message) => {
        console.log(0)
        socket0.emit('confirm', id);
        });

        fetch(variables.API_URL+"get_order/"+id, {
            method: "GET",
            headers: {
                'Accept': 'application/json, text/plain',
                'Content-Type': 'application/json;charset=UTF-8'
            },
            })
            .then(response => response.json())
            .then(result => {
                console.log(result)
                if(result.length > 0){
                    setOrder(result[0])
                }
            }
        )
    }, [])

    async function sendConfirm() {
        // Emit event to the server
        if(start === '1'){
        socket0.emit('confirm', id);
        } else
        socket.emit('confirm_', id);
        // window.location.replace(window.location.origin)
    }

    async function sendCancel() {
        // Emit event to the server
        if(start === '1'){
        socket0.emit('cancel', id);
        } else
        socket.emit('cancel_', id);
        // window.location.replace(window.location.origin)
    }

    if(order !== null){
        return (
            <div>
                <ul>
                {messages}
                </ul>
                {order.create_by === localStorage.getItem('id')?'':<button onClick={(e)=>{sendConfirm(e)}}>confirm</button>}
                <button onClick={(e)=>{sendCancel(e)}}>cancel</button>
            </div>
        );
    }
    
}

export default AppConfirm;
