import { Link, useParams } from "react-router-dom";
import "./../Style/ProfileShop.css";
import { useState, useEffect } from "react";
import {variables} from "../Variables";

function AppProfileShop(){
    const [PS_add_click,PS_add_setClick] = useState(false);
    const PS_add_handleClick = () => PS_add_setClick(!PS_add_click);
    const { id } = useParams();
    const [shop, setShop] = useState([]);
    // const [PS_edit_click,PS_edit_setClick] = useState(false);
    // const PS_edit_handleClick = () => PS_edit_setClick(!PS_edit_click);

    useEffect(() => {
        console.log(id)
        fetch(variables.API_URL+"get_shop/"+id, {
            method: "GET",
            headers: {
                'Accept': 'application/json, text/plain',
                'Content-Type': 'application/json;charset=UTF-8'
            },
            })
            .then(response => response.json())
            .then(result => {
                console.log(result)
                setShop(result[0])
            }
        )
    }, []);

    if(shop.category){
        const category_list = shop.category.map((item, index) => 
        <span>
            {item.category_name}{index === shop.category.length-1?'':','}
        </span>
        );

        const menu_list = shop.menu.map((item, index) => 
        <span>
            {item.category_name}{index === shop.category.length-1?'':','}
        </span>
        );
        
        return(
            <div className='ProfileShop'>
                <div className="PS_shop">
                    <div className="PS_shop_box">
                        <div className="PS_IMG"><img src="https://cwdaust.com.au/wpress/wp-content/uploads/2015/04/placeholder-store.png" alt=""></img></div>
                        <div className="PS_Box">
                            <div className="PS_Box_details">
                                {/* <div className="PS_Box_bg"></div> */}
                                <div className="PS_EditProfile"><Link to="/EditShop/1"><i className="fa-solid fa-pen"></i></Link></div>
                                <div className="PS_name">{shop.shop_name}</div>
                                <div className="PS_frame">
                                    <div><div className="PS_details"><span>Category :</span> {category_list}</div></div>
                                    <div><Link to=""><div className="PS_details"><img src='../img/rate.png' alt='' />-</div></Link></div>
                                    <div><Link to=""><div className="PS_details"><i className="fa-solid fa-phone"></i>{shop.phonenumber}</div></Link></div>
                                    <div className=""><Link to=""><div className="PS_details "><img src='../img/address.png' alt='' />{shop.location_name}</div></Link></div>
                                </div>
                            </div>
                            {/* <div className="PS_button">
                                <Link to="/ProfileShop/1"><div className="PS_button_menu PS_button_active">Menu</div></Link>
                                <div></div>
                                <Link to="/"><div className="PS_button_menu">Rate for Service</div></Link>
                            </div> */}
    
                        </div>
                    </div>
                </div>
                <div className="PS_menu">
                    <div className="PS_menu_Box_button">
                        {shop.create_by.toString() === localStorage.getItem('id').toString()?
                        <Link to="/CreateMenuShop">
                            <div className="PS_menu_button" onClick={PS_add_handleClick}>
                                Create Menu
                            </div>
                        </Link>:''}

                        {menu_list}
                       
                        {/* <div className={PS_add_click ? "PS_add PS_add_active":"PS_add"}>
                            <div className="PS_add_box">
                                <form action="">
                                    <div className="PS_add_img"><img src="https://www.aloexhair.com/wp-content/uploads/2018/06/shutterstock_708886852.jpg" alt=""></img></div>
                                    <div className="PS_add_details">
                                        <div className="PS_add_input"><input className="" name="" id="" placeholder="Name"></input></div>
                                        <div className="PS_add_input"><input className="" name="" id="" placeholder="price (B)"></input></div>
                                        <button type="submit" className="PS_button_submit">
                                            <i className="fa-solid fa-circle-plus"></i> Add
                                        </button>
                                    </div>
                                </form>
                            </div>
                        </div> */}
    
                        
                        {/* <div className={PS_edit_click ? "PS_edit PS_edit_active":"PS_edit"}>
                            <div className="PS_menu_button" onClick={PS_edit_handleClick}>
                                <i className="fa-solid fa-circle-xmark"></i>
                            </div>
                            <form action="">
                                <div className="PS_edit_img"><img src="https://www.aloexhair.com/wp-content/uploads/2018/06/shutterstock_708886852.jpg" alt=""></img></div>
                                <div className="PS_edit_details">
                                    <div className="PS_edit_input"><input className="" name="" id="" placeholder="Name"></input></div>
                                    <div className="PS_edit_input"><input className="" name="" id="" placeholder="price (B)" ></input></div>
                                    <button type="submit" className="PS_button_submit">
                                        <i className="fa-solid fa-circle-plus"></i> edit
                                    </button>
                                </div>
                            </form>
                        </div> */}
                    </div>
                </div>
            </div>
        );
    }
}

export default AppProfileShop;
