import React, { Component } from 'react';
import io from 'socket.io-client';
import { Link, useParams, useNavigate } from "react-router-dom";

class AppConfirm extends Component {
  constructor() {
    super();

    this.state = {
      input: '',
      messages: '',
    };

    // Connect to the Socket.IO server
    this.socket = io('http://localhost:5001');
    this.socket0 = io('http://localhost:5001');
  }

  componentDidMount() {
    this.socket.emit('join', this.state.params.get("id"));

    if(this.state.params.get("start") === '1'){
      console.log(this.state.params.get("start"));
      this.socket0.emit('join', this.state.params.get("id"));
      this.socket0.emit('start', this.state.params.get("id"));
    }

    // Listen for event from the server
    this.socket.on('time', (message) => {
      this.setState({ messages: message });
    });

    this.socket.on('cancel', (message) => {
      this.setState({ messages: message });
    });

    this.socket.on('confirm', (message) => {
      this.setState({ messages: message });
    });

    this.socket.on('msg', (message) => {
      this.setState({ messages: message });
    });

    this.socket0.on('cancel', (message) => {
      console.log(0)
      this.socket0.emit('cancel', this.state.params.get("id"));
    });

    this.socket0.on('confirm', (message) => {
      console.log(0)
      this.socket0.emit('confirm', this.state.params.get("id"));
    });
  }

  componentWillUnmount() {
    // Disconnect from the Socket.IO server
    this.socket.disconnect();
    this.socket0.disconnect();
  }

  sendMessage = () => {
    const { input } = this.state;
    // Emit event to the server
    this.socket.emit('join', input);
    this.setState({ id: input });
  }

  sendConfirm = () => {
    // Emit event to the server
    if(this.state.params.get("start") === '1'){
      this.socket0.emit('confirm', this.state.params.get("id"));
    } else
    this.socket.emit('confirm_', this.state.params.get("id"));
    // window.location.replace(window.location.origin)
  }

  sendCancel = () => {
    // Emit event to the server
    if(this.state.params.get("start") === '1'){
      this.socket0.emit('cancel', this.state.params.get("id"));
    } else
    this.socket.emit('cancel_', this.state.params.get("id"));
    // window.location.replace(window.location.origin)
  }

  handleChange = (e) => {
    this.setState({ input: e.target.value });
  }

  render() {
    const { input, messages } = this.state;
    return (
      <div>
        <input value={input} onChange={this.handleChange} />
        <button onClick={this.sendMessage}>Send</button>
        <button onClick={this.sendConfirm}>confirm</button>
        <button onClick={this.sendCancel}>cancel</button>
        <ul>
          {messages}
        </ul>
      </div>
    );
  }
}

export default AppConfirm;
